mapper.select:

select id,name,dept,email,grade
  from user 
 where id=#{id}

choose.when:

select id,name,dept,email,grade
  from user  
 where grade=#{grade} and dept=#{dept}


select id,name,dept,email,grade
  from user  
 where name=#{name} and dept=#{dept}

choose.otherwise:

select id,name,dept,email,grade
  from user  
 where name=#{name} and grade=#{grade} and dept=#{dept}

mapper.select:

select id,name,dept,email,grade,level
  from user 
 where dept=#{dept}
